mapper.update:
UPDATE
		POST A
	SET 
	    A.POST_CODE = #{postCode} 
      , A.CATEGORY_CODE = #{categoryCode}
      , A.WRITER = #{writer}
      , A.PRICE = #{price}
      , A.ITEM_NAME = #{itemName}
      , A.DELETE_YN = #{deleteYn}
      , A.POST_DATE = SYSDATE 
      , A.POST_CONTEXT = #{postContext}
  WHERE A.POST_CODE = #{postCode}
